endpoint.config:
CREATE SCHEMA adrsearch AUTHORIZATION SA CREATE TABLE adrsearch.addressaccess ( addressaccessid VARCHAR(255) NOT NULL, streetname VARCHAR_IGNORECASE(255) NOT NULL, streetbuildingidentifier VARCHAR_IGNORECASE(5) NOT NULL, postcodeidentifier VARCHAR_IGNORECASE(10) NOT NULL, districtname varchar(50) NOT NULL, presentationstring varchar(325) NOT NULL, geometrywkt varchar(50) NOT NULL, sortorder int, CONSTRAINT addressaccess_pkey PRIMARY KEY (addressaccessid) ) CREATE TABLE adrsearch.streetname ( id varchar(255) NOT NULL, streetname VARCHAR_IGNORECASE(255), postcodeidentifier VARCHAR_IGNORECASE(10) NOT NULL, districtname varchar(50) NOT NULL, presentationstring varchar(325) NOT NULL, CONSTRAINT streetname_pkey PRIMARY KEY (id) );
CREATE INDEX addressaccess_streetname_postcodeIdentifier_sortorder_idx ON adrsearch.addressaccess(streetname, postcodeidentifier, sortorder);
CREATE INDEX addressaccess_streetname_postcodei_streetbuildingidentifier_idx ON adrsearch.addressaccess(streetname, postcodeidentifier, streetbuildingidentifier); CREATE INDEX streetname_streetname_postcodeidentifier_idx ON adrsearch.streetname(streetname, postcodeidentifier);
